script:
frappe.ui.form.on('Opportunity', {
	refresh: function(frm) {
		if(!frm.doc.__islocal){
			frm.add_custom_button(__("Project Estimate"), function(){
				make_project_estimate(frm.doc);
			}, __("Make"));
		}
	}
});

var make_project_estimate=function(doc){
	frappe.call({
					method: "estimation.estimation.utils.make_project_estimate",
					args:{
						opportunity: doc.name
					},
					callback: function(r) {
						if(!r.exc) {
							var project_estimate = frappe.model.sync(r.message);
							frappe.set_route("Form", project_estimate[0].doctype, project_estimate[0].name);
						}
						else{
							frappe.show_alert({
								message:__('No data'),
								indicator:'orange'
							});
						}
					}
				});
}

frappe.ui.form.on('Project', {
	refresh: function(frm) {
		if (frm.doc.total_estimated_cost){
			frm.set_value("estimated_costing", frm.doc.total_estimated_cost)
			refresh_field("estimated_costing");
		}
		if (frm.doc.total_costing_amount){
			frm.set_value("actual_activity_costs", frm.doc.total_costing_amount)
			refresh_field("actual_activity_costs");
		}
		if (frm.doc.total_purchase_cost){
			frm.set_value("actual_purchases_", frm.doc.total_purchase_cost)
			refresh_field("actual_purchases_");
		}
		if (frm.doc.total_expense_claim){
			frm.set_value("actual_expenses", frm.doc.total_expense_claim)
			refresh_field("actual_expenses");
		}

		frm.set_value("actual_total_cost", frm.doc.total_costing_amount + frm.doc.total_purchase_cost + frm.doc.total_expense_claim)
		refresh_field("actual_total_cost");
	}
});
